import React, { Component } from 'react';
import './SearchModal.css';

import { ButtonToolbar, Modal, Nav, Form } from 'react-bootstrap';
import SearchImg from './imgSearchModal/search.svg';

export default class SearchModal extends Component {
	constructor(props, context) {
		super(props, context);

		this.state = {
			lgShow: false
		};
	}

	render() {
		let lgClose = () => this.setState({ lgShow: false });
		return (
			<React.Fragment>
				<Nav.Link className="NavigationLinks" onClick={() => this.setState({ lgShow: true })}>
					<img src={SearchImg} className="searchIcon" alt="search" />
				</Nav.Link>




				<div >
					<Modal
						size="lg"
						show={this.state.lgShow}
						onHide={lgClose}
						aria-labelledby="example-modal-sizes-title-lg"
					>
						<Modal.Body className="p-0 borderRadiusInput">
							<Form.Control
								className="text-center SearchModalInput borderRadiusInput"
								size="lg"
								type="search"
								placeholder="Unesite VaÅ¡u pretragu..."
							/>
						</Modal.Body>
					</Modal>
				</div>



				<div class="modal" tabindex="-1" role="dialog">
					<div class="modal-dialog" role="document">
						<div class="modal-content">
							<div class="modal-header">
								<h5 class="modal-title">Modal title</h5>
								<button type="button" class="close" data-dismiss="modal" aria-label="Close">
									<span aria-hidden="true">&times;</span>
								</button>
							</div>
							<div class="modal-body">
								<p>Modal body text goes here.</p>
							</div>
							<div class="modal-footer">
								<button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
								<button type="button" class="btn btn-primary">Save changes</button>
							</div>
						</div>
					</div>
				</div>



			</React.Fragment>
		);
	}
}
