import React, { Component } from 'react';
import './SearchModal.css';

import { ButtonToolbar, Modal, Nav, Form } from 'react-bootstrap';
import SearchImg from './imgSearchModal/search.svg';

export default class SearchModal extends Component {
	constructor(props, context) {
		super(props, context);

		this.state = {
			lgShow: false
		};
	}

	render() {
		let lgClose = () => this.setState({ lgShow: false });
		return (
			<React.Fragment>
				<Nav.Link className="NavigationLinks" onClick={() => this.setState({ lgShow: true })}>
					<img src={SearchImg} className="searchIcon" alt="search" />
				</Nav.Link>

				<div className="borderRadiusInput">
					<Modal
						className="borderRadiusInput ModalTopMargin"
						size="lg"
						show={this.state.lgShow}
						onHide={lgClose}
						aria-labelledby="example-modal-sizes-title-lg"
					>
						<Modal.Body className="p-0 borderRadiusInput">
							<Form.Control
								className="text-center SearchModalInput borderRadiusInput"
								size="lg"
								type="search"
								placeholder="Unesite Vašu pretragu..."
							/>
						</Modal.Body>
					</Modal>
				</div>

				<div className="modal ModalTopMargin" tabIndex={-1} role="dialog">
					<div className="modal-dialog " role="document">
						<div className="modal-content">
							<div className="modal-header">
								<h5 className="modal-title">Modal title</h5>
								<button type="button" className="close" data-dismiss="modal" aria-label="Close">
									<span aria-hidden="true">×</span>
								</button>
							</div>

							<div className="modal-body p-0 borderRadiusInput">
								<p>Modal body text goes here.</p>
							</div>

							<div className="modal-footer">
								<button type="button" className="btn btn-secondary" data-dismiss="modal">
									Close
								</button>
								<button type="button" className="btn btn-primary">
									Save changes
								</button>
							</div>
						</div>
					</div>
				</div>
			</React.Fragment>
		);
	}
}
