import React, { Component } from 'react';
import { ButtonToolbar, Button, Modal, Nav } from 'react-bootstrap';

export default class SearchModal extends Component {
	constructor(props, context) {
		super(props, context);

		this.state = {
			lgShow: false
		};
	}

	render() {
		let lgClose = () => this.setState({ lgShow: false });
		return (
			<ButtonToolbar>
				<Nav.Link className="NavigationLinks" onClick={() => this.setState({ lgShow: true })}>
					<img src={SearchImg} className="searchIcon" alt="search" />
				</Nav.Link>
				<Button onClick={() => this.setState({ lgShow: true })}>Large modal</Button>
				<Modal
					size="lg"
					show={this.state.lgShow}
					onHide={lgClose}
					aria-labelledby="example-modal-sizes-title-lg"
				>
					<Modal.Header closeButton>
						<Modal.Title id="example-modal-sizes-title-lg">Large Modal</Modal.Title>
					</Modal.Header>
					<Modal.Body>...</Modal.Body>
				</Modal>
			</ButtonToolbar>
		);
	}
}
