import React, { Component } from 'react';
import { ButtonToolbar, Modal, Nav } from 'react-bootstrap';
import SearchImg from './imgSearchModal/search.svg';

export default class SearchModal extends Component {
	constructor(props, context) {
		super(props, context);

		this.state = {
			lgShow: false
		};
	}

	render() {
		let lgClose = () => this.setState({ lgShow: false });
		return (
			<ButtonToolbar>
				<Nav.Link className="NavigationLinks" onClick={() => this.setState({ lgShow: true })}>
					<img src={SearchImg} className="searchIcon" alt="search" />
				</Nav.Link>
				<Modal
					size="lg"
					show={this.state.lgShow}
					onHide={lgClose}
					aria-labelledby="example-modal-sizes-title-lg"
				>
					<Modal.Header closeButton>
						<Modal.Title id="example-modal-sizes-title-lg">Large Modal</Modal.Title>
					</Modal.Header>
					<Modal.Body>
						<>
  <Form.Control size="lg" type="text" placeholder="Large text" />
  <br />
  <Form.Control type="text" placeholder="Normal text" />
  <br />
  <Form.Control size="sm" type="text" placeholder="Small text" />
</>
					</Modal.Body>
				</Modal>
			</ButtonToolbar>
		);
	}
}
